import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { firebase } from './src/firebase/config';
import Detail from './src/Detail/Detail'
import Login from './src/Login/Login';
import Register from './src/Register/Register';
import NavTab from './src/NavTab/NavTab';
import NavBar from './src/NavTab/NavBar';
import Overview from './src/Overview';


const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

export default function App() {

  let [isLogin, setIsLogin] = useState(false);
  let [isLoading, setLoading] = useState(true);
  let [user, setUser] = useState();

  useEffect(() => {
    const usersRef = firebase.firestore().collection('users');
    firebase.auth().onAuthStateChanged(user => {
      if (user) {
        usersRef
          .doc(user.uid)
          .get()
          .then((document) => {
            const userData = document.data()
            setUser(userData)
            setLoading(false)
            setIsLogin(true)
          })
          .catch((error) => {
            setLoading(false)
          });
      } else {
        setLoading(false)
      }
    });
  }, []);

  return (
    <NavigationContainer>
              <NavBar />
      {/* Icons for navigation tabs */}
      <Tab.Navigator screenOptions={NavTab} tabBarOptions={{
        activeTintColor: '#333333',
        inactiveTintColor: 'gray',
      }}>
        {!isLogin && <Tab.Screen name="Login" children={() => !isLoading && <Login setIsLogin={setIsLogin} setUser={setUser} />} />}
        {!isLogin && <Tab.Screen name="Register" children={() => !isLoading && <Register setIsLogin={setIsLogin} setUser={setUser} />} />}
        {isLogin && <Tab.Screen name="Overview" children={() => <Overview user={user} />} />}
        {isLogin && <Tab.Screen name="Map" component={Overview} user={user} />}
        {isLogin && <Tab.Screen name="Settings" component={Overview} user={user} />}
        <Stack.Screen name="Detail" component={Detail} />
      </Tab.Navigator>
      {/* <Stack.Navigator initialRouteName="Home">


      </Stack.Navigator> */}
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
