import React, { useState, useEffect } from 'react';
import { firebase } from './firebase/config';
import { SafeAreaView, StyleSheet, View, FlatList, Image, Button } from 'react-native';

const Overview = (props) => {
  const [parksData, setParksData] = useState([]);

  const entityRef = firebase.firestore().collection('parks')
  const userID = props.user.id

  useEffect(() => {
      entityRef.where("userID", "==", userID).onSnapshot(query => {
                let parks = []
                query.forEach(park => {
                    parks.push({id: park.id, src: park.data().imgURL })
                });
                setParksData(parks);
                console.log(parks);
            },
            error => {
                console.log(error)
            }
        )
  }, [])

  let addPark = () => {
    let data = {
      name: "park",
      imgURL: "https://cf.bstatic.com/data/xphoto/1182x887/324/32450911.jpg?size=S",
      userID,
    }
    entityRef.add(data).catch((error) => {
        alert(error)
    });

  }
  return (
    <SafeAreaView style={styles.container}>
      <FlatList
        data={parksData}
        renderItem={({ item }) => (
          <View style={{ flex: 1, flexDirection: 'column', margin: 1 }}>
            <Image style={styles.imageThumbnail} source={{ uri: item.src }} />
            {/* <Button onPress={addPark} title="Add" /> */}
          </View>
        )}
        //Setting the number of column
        numColumns={3}
        keyExtractor={(item, index) => index}
      />
    </SafeAreaView>
  );
};
export default Overview;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: 'white',
  },
  imageThumbnail: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 100,
  },
});
