import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { firebase } from '../firebase/config';


import Login from './src/Login/Login';
import Register from './src/Register/Register';
import NavTab from './src/NavTab/NavTab';
import Overview from './src/Overview';


const Tab = createBottomTabNavigator();

export default function App() {

  let [isLogin, setIsLogin] = useState(false);
  let [user, setUser] = useState();

  useEffect(() => {
    const usersRef = firebase.firestore().collection('users');
    firebase.auth().onAuthStateChanged(user => {
      if (user) {
        usersRef
          .doc(user.uid)
          .get()
          .then((document) => {
            const userData = document.data()
            setIsLogin(true)
            setUser(userData)
          })
          .catch((error) => {
            setIsLogin(false)
          });
      } else {
        setIsLogin(false)
      }
    });
  }, []);

  return (
    <NavigationContainer>
    {/* Icons for navigation tabs */}
    <Tab.Navigator screenOptions={NavTab} tabBarOptions={{
        activeTintColor: '#333333',
        inactiveTintColor: 'gray',
      }}>
      {!isLogin && <Tab.Screen name="Login" children={() => <Login setIsLogin={setIsLogin}/>} />}
      {!isLogin && <Tab.Screen name="Register" children={() => <Register setIsLogin={setIsLogin}/>} />}
      {isLogin && <Tab.Screen name="Overview" component={Overview} />}
      {isLogin && <Tab.Screen name="Map" component={Overview} />}
      {isLogin && <Tab.Screen name="Settings" component={Overview} />}
    </Tab.Navigator>
  </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
