import React, { useState, useEffect } from 'react';
import { firebase } from '../firebase/config';
import { SafeAreaView, StyleSheet, View, Text, FlatList, Image, Button } from 'react-native';

const Detail = ({navigation, route}) => {
 const park = route.params.item;
 console.log(park.src);
  return (
    <View style={styles.container}>
      <Image style={styles.imageThumbnail} source={{ uri: park.src }} />
      <Text style={styles.textName}>{park.name}</Text>
      <Text style={styles.textDesctiption}>{park.description}</Text>
      <Text style={styles.textLocation}>{park.loacation}</Text>
  </View>
  );
};
export default Detail;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
  },
  imageThumbnail: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 70,
    borderRadius: 90,
  },
  textName: {
    textAlign: 'center',
    fontSize: 17,
  },
  textDesctiption: {
    textAlign: 'center',
    color: 'gray',
  },
  textLocation: {
    color: 'gray',
    textAlign: 'center',
  }
});
