import React, { useState, useEffect } from 'react';
import { firebase } from '../firebase/config';
import { StyleSheet, View } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import MapView, { Marker, PROVIDER_GOOGLE } from 'react-native-maps';


const Map = (props) => {

    let [parksData, setParksData] = useState([]);
    const entityRef = firebase.firestore().collection('parks')
    const userID = props.user.id

    useEffect(() => {
        entityRef.where("userID", "==", userID).onSnapshot(query => {
            let parks = [];
            query.forEach(park => {
                let data = park.data();
                parks.push({
                    id: park.id,
                    src: data.src,
                    name: data.name,
                    description: data.description,
                    loacation: data.loacation,
                    geo: data.geo,
                })
            });
            setParksData(parks);
        },
            error => {
                console.log(error)
            }
        )
    }, [])

    let getMarkers = () => {
        return parksData.map((item) => {
            {console.log(item)}
            <Marker key={item.id} coordinate={item.geo} title={item.name}
                description={item.description} onPress={() => navigation.push("Detail", { item })} />
        });
    }

    return (
        <MapView style={{ flex: 1 }} provider={PROVIDER_GOOGLE}
            showsUserLocationinitialRegion={{
                latitude: 37.78825,
                longitude: -122.4324,
                latitudeDelta: 0.0922,
                longitudeDelta: 0.0421,
            }}>
            {getMarkers()}
        </MapView>

    );
};
export default Map;

const styles = StyleSheet.create({

});
