import React, { useState } from 'react';
import { createStackNavigator } from '@react-navigation/stack';
import { useNavigation } from '@react-navigation/native';
import { Button, Text, View, StyleSheet, Switch, TouchableWithoutFeedback, Image } from 'react-native';
import i18n from '../i18n'

const Stack = createStackNavigator();

const Settings = (props) => {

    const [isEnabled, setIsEnabled] = useState(false);
    let [lng, setLng] = useState(props.lng);
    const toggleSwitch = () => {
        setIsEnabled(previousState => !previousState);
        props.setIsDark(isEnabled);
    };
    

    let changeLngToRus = () => {
        i18n.locale = "ru";
        props.setLng('ru');
        setLng('ru');
    }

    let changeLngToEng = () => {
        i18n.locale = "en";
        props.setLng('en');
        setLng('en');
    }
    return (
        <View style={styles.container}>
            <View style={styles.itemContainer}>
            <TouchableWithoutFeedback onPress={changeLngToRus}>
                <Image style={styles.imageThumbnail} source={{ uri: "https://megabuzz.ru/image/flags/flag-rus.png" }} />
            </TouchableWithoutFeedback>
            <TouchableWithoutFeedback onPress={changeLngToEng}>
                <Image style={styles.imageThumbnail} source={{ uri: "https://anastasiafond.ru/wp-content/uploads/2018/04/En.png" }} />
            </TouchableWithoutFeedback>
            </View>
            <View style={styles.itemContainer}>
                <Text>{i18n.t("def")}</Text>
            <Switch
                trackColor={{ false: "gray", true: "lime" }}
                thumbColor={isEnabled ? "white" : "white"}
                ios_backgroundColor="#3e3e3e"
                onValueChange={toggleSwitch}
                value={isEnabled}
            />
            <Text>{i18n.t("dark")}</Text>
            </View>

        </View>
    );
}

export default Settings;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: 'white',
        justifyContent: 'space-around'
    },
    imageThumbnail: {
        justifyContent: 'center',
        alignItems: 'center',
        height: 100,
        width: 100,
        borderRadius: 90,
    },
    itemContainer: {
        flexDirection: 'row',
    },
    textName: {
        textAlign: 'center',
        fontSize: 17,
    },
    textDesctiption: {
        textAlign: 'center',
        color: 'gray',
    },
    textLocation: {
        color: 'gray',
        textAlign: 'center',
    }
});
