import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { firebase } from '../firebase/config'
import { StyleSheet, TextInput, Button, View } from 'react-native';

let Register = ({navigation, route}) => {
  
  let [login, setLogin] = useState('');
  let [email, setEmail] = useState('');
  let [password, setPassword] = useState('');
  let [age, setAge] = useState();
  let [phone, setPhone] = useState('+375');

  let onRegisterPress = () => {
    debugger;
    navigation.navigate('Login');
    firebase
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then((response) => {
        const uid = response.user.uid
        const data = {
          id: uid,
          login,
          email,
          age,
          phone,
        };
        const usersRef = firebase.firestore().collection('users')
        usersRef
          .doc(uid)
          .set(data)
          .then(() => {
            props.setIsLogin(true);
            navigation.navigate('Overview', { user: data })
          })
          .catch((error) => {
            alert(error)
          });
      })
      .catch((error) => {
        alert(error)
      });
  }

  return (
    <View style={styles.container}>
      <TextInput style={styles.text} placeholder="Login" value={login} onChange={(e) => setLogin(e.target.value)} />
      <TextInput secureTextEntry={true} style={styles.text} 
                                  placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <TextInput style={styles.text} placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <TextInput style={styles.text} placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <TextInput style={styles.text} placeholder="Age"   value={age} onChange={(e) =>  setAge(e.target.value)} />
      <Button title="Register" color="#333333" onPress={onRegisterPress}/>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',

  },
  text: {
    borderBottomColor: 'black',
    borderBottomWidth: 2,
    borderStyle: 'solid',
    width: 200,
    marginVertical: 5,
    color: '#333333',
    fontSize: 15,
    marginVertical: 10,
  },
});

export default Register;
