import React, { useState, useEffect } from 'react';
import { firebase } from '../firebase/config';
import { SafeAreaView, StyleSheet, View, Text, FlatList, Image, ScrollView, TouchableWithoutFeedback } from 'react-native';

const Photos = ({ navigation, route }) => {
    const [modalVisible, setModalVisible] = useState(false);
    const photos = route.params.photos;
    return (
        <SafeAreaView style={styles.container}>
                  <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Hello World!</Text>
            <Pressable
              style={[styles.button, styles.buttonClose]}
              onPress={() => setModalVisible(!modalVisible)}
            >
              <Text style={styles.textStyle}>Hide Modal</Text>
            </Pressable>
          </View>
        </View>
      </Modal>
            <FlatList data={photos} renderItem={({ item }) => (
                <View style={{ flex: 1, flexDirection: 'column', margin: 1 }}>
                    <TouchableWithoutFeedback onPress={() => navigation.push("Detail", { item })}>
                        <View style={styles.itemContainer}>
                            <Image style={styles.imageThumbnail} source={{ uri: item.src }} />
                        </View>
                    </TouchableWithoutFeedback>
                </View>
            )} numColumns={3} keyExtractor={(item, index) => index} />
        </SafeAreaView>

    );
};
export default Photos;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        backgroundColor: 'white',
    },
    imageThumbnail: {
        justifyContent: 'center',
        alignItems: 'center',
        height: 100,
        width: 100,
        borderRadius: 3,
    },
    itemContainer: {
        alignItems: 'center',
        borderColor: 'gray',
        borderStyle: 'solid',
    },
});
