import React, { useState, useEffect } from 'react';
import { firebase } from './firebase/config';
import { SafeAreaView, StyleSheet, View, FlatList, Image } from 'react-native';

const Overview = (props) => {
  const [parksData, setParksData] = useState([]);

  const entityRef = firebase.firestore().collection('parks')
  const userID = props.user.id

  useEffect(() => {
      entityRef
          .where("userID", "==", userID)
          .onSnapshot(
            query => {
                let parks = []
                query.forEach(park => {
                  console.log(park);
                    parks.push({id: park.id, src: park.imgURL })
                });
                setParksData(parks);
                console.log(parks);
            },
            error => {
                console.log(error)
            }
        )
  }, [])

  // useState(() => {
  //   let items = Array.apply(null, Array(60)).map((v, i) => {
  //     return { id: i, src: 'https://travel.home.sndimg.com/content/dam/images/travel/fullset/2015/05/14/101-amazing-thrills-tg/cedar-point-sandusky-ohio.jpg.rend.hgtvcom.1280.960.suffix/1491582056026.jpeg' };
  //   });
  //   setDataSource(items);
  // }, []);

  return (
    <SafeAreaView style={styles.container}>
      <FlatList
        data={parksData}
        renderItem={({ item }) => (
          <View style={{ flex: 1, flexDirection: 'column', margin: 1 }}>
            <Image style={styles.imageThumbnail} source={{ uri: item.src }} />
          </View>
        )}
        //Setting the number of column
        numColumns={3}
        keyExtractor={(item, index) => index}
      />
    </SafeAreaView>
  );
};
export default Overview;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: 'white',
  },
  imageThumbnail: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 100,
  },
});
