const Detail = ({navigation, route}) => {
 const park = route.params.item;
 console.log(park.src);
  return (
    <View style={styles.container}>
      <Image style={styles.imageThumbnail} source={{ uri: park.src }} />
      <Text style={styles.textName}>{park.name}</Text>
      <Text style={styles.textDesctiption}>{park.description}</Text>
      <Text style={styles.textLocation}>{park.loacation}</Text>

      <SafeAreaView style={styles.container}>
        <FlatList data={parksData} renderItem={({ item }) => (
            <View style={{ flex: 1, flexDirection: 'row', margin: 1 }}>
              <TouchableWithoutFeedback onPress={() => navigation.push("Detail", {item})}>
                <View style={styles.itemContainer}>
                  <Image style={styles.imageThumbnail} source={{ uri: item.src }} />
                  <Text style={styles.textName}>{item.name}</Text>
                  {/* <Text style={styles.textDesctiption}>{item.description}</Text> */}
                  <Text style={styles.textLocation}>{item.loacation}</Text>
                </View>
              </TouchableWithoutFeedback>
            </View>
          )} numColumns={3} keyExtractor={(item, index) => index} />
          {/* <Button title="add" onPress={addPark}/> */}
      </SafeAreaView>

  </View>
  );
};
export default Detail;

const styles = StyleSheet.create({
